import datafrom typing import Optionalfrom dataclasses import dataclassdef county_consumption(counties: list[dict], county_name: str)-> float:    #Gives us the total energy consumption (residential, commercial, and industrial) of the county.    #We have counties as the list    total = 0    for electric in counties:       if electric["county_name"].lower() == county_name.lower():           total += electric["res_consumption_mwh"] + electric["commercial_consumption_mwh"] + electric["ind_consumption_mwh"]    return totaldef county_res_average(counties: list[dict], county_name: str) -> Optional[float]:    for c in counties:        if c["county_name"].lower() == county_name.lower():            return round((c["res_consumption_mwh"]*1000 / c["population"]) / 365,1)    return print("Error: County not in Top 20")def commercial_usage(counties:list[dict], county_name: str)-> Optional[float]:    total = 0    for electric in counties:        if electric["county_name"].lower() == county_name.lower():            try:                total += electric["commercial_consumption_mwh"]/366            except KeyError:                print("Wrong Input. Try again.")    return round(total,1)def industrial_usage(counties:list[dict], county_name: str)-> Optional[float]:    total = 0    for electric in counties:        if electric["county_name"].lower() == county_name.lower():            try:                total += electric["ind_consumption_mwh"]/366            except KeyError:                print("Wrong Input. Try again.")    return round(total,1)def energy_tips(user_type: str):    if user_type == '1':  # Residential        print("Residential Energy Saving Tips:")        print("- Turn off lights and appliances when not in use.")        print("- Use energy-efficient LED bulbs.")        print("- Unplug devices that draw standby power.")        print("- Use a programmable thermostat to save on heating/cooling.")        print("- Limit use of high-energy appliances during peak hours.\n")    elif user_type == '2':  # Commercial        print("Commercial Energy Saving Tips:")        print("- Upgrade to energy-efficient lighting and HVAC systems.")        print("- Schedule equipment and lights to turn off after hours.")        print("- Conduct an energy audit to identify inefficiencies.")        print("- Encourage employees to save energy (computer shutdowns, lights off).")        print("- Use smart meters to monitor and optimize usage.\n")    elif user_type == '3':  # Industrial        print("Industrial Energy Saving Tips:")        print("- Optimize production schedules to reduce peak energy use.")        print("- Upgrade machinery to energy-efficient models.")        print("- Implement preventive maintenance to reduce energy waste.")        print("- Recover waste heat or energy where possible.")        print("- Monitor energy consumption regularly with automated systems.\n")# ------------------------- Main Program -------------------------def main():    print("Energy Usage Comparison Tool")    print("Top 20 Electricity Consuming Counties in California\n")    while True:        county = input("Enter your county:").strip()        # Make sure county is in Top 20        twenty_counties = [c["county_name"].lower() for c in data.counties]        if county.lower() not in twenty_counties:            print("Error: County not in top 20. Try again.\n")            continue        # Ask user type        print("\nAre you a:")        print("1. Residential user")        print("2. Commercial user")        print("3. Industrial user")        user_type = input("Choose 1â€“3: ").strip()        if user_type not in ['1', '2', '3']:            print("Invalid choice. Try again.\n")            continue        # Ask for user's usage        try:            if user_type == '1':                user_usage = float(input("Enter your daily residential usage (kWh per day): "))                county_avg = county_res_average(data.counties, county)                unit = "kWh"            elif user_type == '2':                user_usage = float(input("Enter your daily commercial usage (MWh per day): "))                county_avg = commercial_usage(data.counties, county)                unit = "MWh"            elif user_type == '3':                user_usage = float(input("Enter your daily industrial usage (MWh per day): "))                county_avg = industrial_usage(data.counties, county)                unit = "MWh"        except ValueError:            print("Invalid input. Please enter a number.\n")            continue        if county_avg is None:            print("Error: Could not find county data.\n")            continue        # Compare user usage to county average        print(f"\nCounty average daily usage: {county_avg} {unit}")        print(f"Your daily usage: {user_usage} {unit}")        if user_usage > county_avg:            diff = round((user_usage - county_avg) / county_avg * 100, 1)            print(f"You are above the county average by {diff}%.")            # Show tips            energy_tips(user_type)        elif user_usage < county_avg:            diff = round((county_avg - user_usage) / county_avg * 100, 1)            print(f"You are below the county average by {diff}%. Great job!")        else:            print("You match the county average exactly!\n")if __name__ == "__main__":    main()