import datafrom typing import Optionaldef county_consumption(counties: list[dict], county_name: str)-> float:    #Gives us the total energy consumption (residential, commercial, and industrial) of the county.    #We have counties as the list    total = 0    for electric in counties:       if electric["county_name"].lower() == county_name.lower():           total += electric["res_consumption_mwh"] + electric["commercial_consumption_mwh"] + electric["ind_consumption_mwh"]    return totaldef county_res_average(counties: list[dict], county_name: str) -> Optional[float]:    for c in counties:        if c["county_name"].lower() == county_name.lower():            return round((c["res_consumption_mwh"]*1000 / c["population"]) / 365,1)    return print("Error: County not in Top 20")def commercial_usage(counties:list[dict], county_name: str)-> Optional[float]:    total = 0    for electric in counties:        if electric["county_name"].lower() == county_name.lower():            try:                total += electric["commercial_consumption_mwh"]/366            except ValueError:                print("Wrong Input. Try again.")    return round(total,1)def industrial_usage(counties:list[dict], county_name: str)-> Optional[float]:    total = 0    for electric in counties:        if electric["county_name"].lower() == county_name.lower():            try:                total += electric["ind_consumption_mwh"]/366            except ValueError:                print("Wrong Input. Try again.")    return round(total,1)def energy_tips(user_type: str):    if user_type == '1':  # Residential        print("Residential Energy Saving Tips:")        print(" - Turn off lights and appliances when not in use,\n - Use energy-efficient LED bulbs,\n - Limit use of high-energy appliances during peak hours,\n - Use a programmable thermostat to save on heating/cooling.")    elif user_type == '2':  # Commercial        print("Commercial Energy Saving Tips:")        print(" - Upgrade to energy-efficient lighting and HVAC systems,\n - Schedule equipment and lights to turn off after hours,\n - Encourage employees to save energy.")    elif user_type == '3':  # Industrial        print("Industrial Energy Saving Tips:")        print(" - Optimize production schedules to reduce peak energy use,\n - Upgrade machinery to energy-efficient models,\n - Implement preventive maintenance to reduce energy waste,\n - Recover waste heat or energy where possible.")#Main Programdef main():    print("Energy Usage Comparison Tool in the Top 20 Electricity Consuming Counties in California\n ")    while True:        county = input("Enter your county (or type q to quit):").strip()        # Make sure county is in Top 20        twenty_counties = [c["county_name"].lower() for c in data.counties]        if county.lower() == "q":            print("Quitting Program, Have a nice day!")            break        elif county.lower() not in twenty_counties:            print("Error: County not in top 20. Try again.\n")            continue        while True:            # Ask user type            print("\nAre you a:")            print("1. Residential user")            print("2. Commercial user")            print("3. Industrial user")            user_type = input("Choose 1â€“3: ").strip()            if user_type not in ['1', '2', '3']:                print("Invalid choice. Try again.")                continue            # Ask for user's usage            while True:                try:                    if user_type == '1':                        user_usage = float(input("Enter your daily residential usage (kWh per day): "))                        county_avg = county_res_average(data.counties, county)                        unit = "kWh"                    elif user_type == '2':                        user_usage = float(input("Enter your daily commercial usage (MWh per day): "))                        county_avg = commercial_usage(data.counties, county)                        unit = "MWh"                    elif user_type == '3':                        user_usage = float(input("Enter your daily industrial usage (MWh per day): "))                        county_avg = industrial_usage(data.counties, county)                        unit = "MWh"                except ValueError:                    print("Invalid input. Please enter a number.\n")                    continue                # Compare user usage to county average                print(f"\nCounty average daily usage: {county_avg} {unit}")                print(f"Your daily usage: {user_usage} {unit}")                if user_usage > county_avg:                    diff = round((user_usage - county_avg) / county_avg * 100, 1)                    print(f"You are above the county average by {diff}%.")                    # Show tips                    energy_tips(user_type)                    return                elif user_usage < county_avg:                    diff = round((county_avg - user_usage) / county_avg * 100, 1)                    print(f"You are below the county average by {diff}%. Great job!")                    return                else:                    print("You match the county average exactly!\n")                    returnif __name__ == "__main__":    main()